import React, { Component } from 'react';
import * as d3 from 'd3';

class LineChart extends Component {

    constructor(props){
        super(props)
        this.myRef = React.createRef();
    }
	

    componentDidMount () {

		var margin = { top: 50, left: 60, bottom: 15, right: 0 },
            width = 700 - margin.left - margin.right,
            height = 500 - margin.top - margin.bottom;

var svg = d3.select(".test-container").append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom);

var y = d3.scaleLinear()
    .range([height - margin.bottom, margin.top])

var x = d3.scaleBand()
    .range([margin.left, width - margin.right])
    .paddingOuter(0.4)
    .paddingInner(0.3)


//DEFINIENDO ESTILO PARA EJE X, EJE Y Y BARRAS
var xAxisGroup = svg.append("g")
    .attr("class", "x axis")
    .attr("color", "black")
    .attr("transform", "translate(0, " + (height - 15) + ")");

var yAxisGroup = svg.append("g")
    .attr("class", "y axis")
    .attr("color", "black");

//IMPORTANDO LOS DATOS
d3.csv('https://raw.githubusercontent.com/diazmonica/covid/main/Book1.csv').then(data => {
    data = data.map(({ Mes, valor }) => ({ x: Mes, value: valor }));
    data = [
        {x: '24-Apr-07', value: 93.24},
        {x: '24-Apr-07', value: 52.24},
        {x: '25-Apr-07', value: 95.35 },
        {x: '25-Apr-07', value: 65.35 },
        {x: '26-Apr-07', value: 98.84 },
        {x: '27-Apr-07', value: 99.92 },
        {x: '30-Apr-07', value: 99.80 },
        {x: '1-May-07', value: 99.47}
      ];
    data.forEach(d => d.value = +d.value);

    x.domain(data.map(d => d.x));
    y.domain([0, d3.max(data, d => d.value)])

    //DEFINIENDO X
    var xAxis = d3.axisBottom(x);
    xAxisGroup
        .call(xAxis)
        .selectAll("text")
        .style("text-anchor", "end")
        .attr("dy", "0.9em")
        .attr("transform", "rotate(-35)");

    //DEFINIENDO Y
    var yAxis = d3.axisLeft(y)
        .ticks(10)
        .tickFormat(d => d)
    yAxisGroup
        .call(yAxis)
        .attr("transform", "translate(" + margin.left + ",0)");


    //CREANDO EL TOOLTIP
    var tooltip = d3.select("#pruebas")
    .append("div")
    .style("position", "absolute")
    .style("z-index", 10)
	.style("visibility", "visible")
		console.log('====================================');
		console.log(data);
		console.log('====================================');
    //AGREGANDO BARRAS Y TOOLTIP
    var bar = svg.append("g")
    .selectAll("rect")
    .data(data)
    .enter()
    .append("rect")
    .attr("x", d => x(d.x))
    .attr("y", d => y(d.value))
    .attr("width", x.bandwidth())
    .attr("height", d => y(0) - y(d.value))
    .attr("fill", '#5499c7')
    .text(d => d.value)
	.on('mouseover', () => {
        
    })
    .on('mouseout',  d => {
        tooltip.text(d.x + " : " + (d.value) + " pruebas");
    })

	

});
    }

    render() {
        return <div id="pruebas" style={{'background': 'white'}}>
		<div className="test-container">
		  <div className="filter">
		  </div>
	  </div>
	  </div>
    };
}
export default LineChart;
