import React, { Component } from 'react';
import * as d3 from 'd3';
import moment from 'moment';

class LineChart extends Component {

    constructor(props){
        super(props)
        this.myRef = React.createRef();
    }
	

    componentDidMount () {

		var margin = { top: 50, left: 60, bottom: 15, right: 0 },
            width = 700 - margin.left - margin.right,
            height = 500 - margin.top - margin.bottom;

var svg = d3.select(".test-container").append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom);

var y = d3.scaleLinear()
    .range([height - margin.bottom, margin.top])

var x = d3.scaleBand()
    .range([margin.left, width - margin.right])
    .paddingOuter(0.4)
    .paddingInner(0.3)


//DEFINIENDO ESTILO PARA EJE X, EJE Y Y BARRAS
var xAxisGroup = svg.append("g")
    .attr("class", "x axis")
    .attr("color", "black")
    .attr("transform", "translate(0, " + (height - 15) + ")");

var yAxisGroup = svg.append("g")
    .attr("class", "y axis")
    .attr("color", "black");

//IMPORTANDO LOS DATOS
d3.json ("http://api.openweathermap.org/data/2.5/forecast?q=Medellin&lang=sp&units=metric&appid=f0e5a162320155ed3912e1cbaa66d4b2").then (function (data){
    data = data.list;
    var datos = [];
    var i = 0;
    data.forEach(d => {
        var fecha = moment.unix(d.dt).format("DD/MMM/YY")
        
            datos.push({date: fecha, tempL: d.main.temp_min, tempH: d.main.temp_max})
            var ultimoRegistro = datos[datos.length-1];
            if(fecha == ultimoRegistro.date){
                if(d.main.temp_min < ultimoRegistro.tempL){
                    datos[datos.length-1].tempL = d.main.temp_min;
                }
                if(d.main.temp_max > ultimoRegistro.tempH){
                    datos[datos.length-1].tempH = d.main.temp_max;
                }
            }else{
                datos.push({date: fecha, tempL: d.main.temp_min, tempH: d.main.temp_max})
            }

    });
    console.log('========fin============================');
    console.log(datos);
    console.log('========fin============================');

    data = [
        {date: '24-Apr-07', tempL: 52.24, tempH: 93.24},
        {date: '25-Apr-07', tempL: 65.23, tempH: 95.35},
        {date: '26-Apr-07', tempL: 58.65, tempH: 98.84},
        {date: '27-Apr-07', tempL: 62.52, tempH: 99.92},
        {date: '30-Apr-07', tempL: 61.87, tempH: 99.80},
        {date: '01-May-07', tempL: 59.12, tempH: 99.47}
      ];
    
    data = datos;

    x.domain(data.map(d => d.date));
    y.domain([0, d3.max(data, d => d.tempH)])

    //DEFINIENDO X
    var xAxis = d3.axisBottom(x);
    xAxisGroup
        .call(xAxis)
        .selectAll("text")
        .style("text-anchor", "end")
        .attr("dy", "0.9em")
        .attr("transform", "rotate(-35)");

    //DEFINIENDO Y
    var yAxis = d3.axisLeft(y)
        .ticks(10)
        .tickFormat(d => d)
    yAxisGroup
        .call(yAxis)
        .attr("transform", "translate(" + margin.left + ",0)");


    //CREANDO EL TOOLTIP
    var tooltip = d3.select("#pruebas")
    .append("div")
    .style("position", "absolute")
    .style("z-index", 10)
	.style("visibility", "visible")

    //AGREGANDO BARRAS Y TOOLTIP
    svg.append("g")
    .selectAll("rect")
    .data(data)
    .enter()
    .append("rect")
    .attr("x", d => x(d.date))
    .attr("y", d => y(d.tempL))
    .attr("width", 30)
    .attr("height", d => y(0) - y(d.tempL))
    .attr("fill", '#5499c7')
    .text(d => d.tempL)
	.on('mouseover', d => {
        tooltip.text(d.srcElement.textContent); 
        return tooltip.style("visibility", "visible")
            .style("top", (d.pageY) + "px").style("left", (d.pageX) + "px").style("background-color", "white")
            .style("border", "1px solid black").style("border-radius", "0.5cm").style("padding", "5px")
    })
    .on("mouseout", () => tooltip.style("visibility", "hidden"))

    svg.append("g")
    .selectAll("rect")
    .data(data)
    .enter()
    .append("rect")
    .attr("x", d => x(d.date)+30)
    .attr("y", d => y(d.tempH))  
    .attr("width", 30)
    .attr("height", d => y(0) - y(d.tempH))
    .attr("fill", 'red')
    .text(d => d.tempH)
	.on('mouseover', d => {
        tooltip.text(d.srcElement.textContent); 
        return tooltip.style("visibility", "visible")
            .style("top", (d.pageY) + "px").style("left", (d.pageX) + "px").style("background-color", "white")
            .style("border", "1px solid black").style("border-radius", "0.5cm").style("padding", "5px")
    })
    .on("mouseout", () => tooltip.style("visibility", "hidden"))

	

});
    }

    render() {
        return <div id="pruebas" style={{'background': 'white'}}>
		<div className="test-container">
		  <div className="filter">
		  </div>
	  </div>
	  </div>
    };
}
export default LineChart;
